declare global {
  interface Window {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    PinUtils: any;
  }
}

type Props = {
  src?: string;
};

export const PinterestSaveButton = ({ src }: Props) => {
  if (!src) return null;
  const onClick = () => {
    window.PinUtils.pinOne({
      media: src,
    });
  };
  return (
    <div>
      <span
        className="bg-[#e60023] text-white flex cursor-pointer badge left-badge h-6 w-12"
        onClick={onClick}
        style={{
          background:
            '#e60023 url(data:image/svg+xml;base64,) 3px 50% no-repeat',
          textIndent: '20px',
          backgroundSize: '14px 14px',
          fontSize: '11px',
          padding: '0 4px 0 0',
          borderRadius: '2px',
        }}
      >
        Save
      </span>
    </div>
  );
};
