import { cn } from '@src/lib/helpers/helper';

declare global {
  interface Window {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    PinUtils: any;
  }
}

type Props = {
  src?: string;
  className?: string;
};

export const PinterestSaveButton = ({ className, src }: Props) => {
  if (!src) return null;
  const onClick = () => {
    window.PinUtils.pinOne({
      media: src,
    });
  };
  return (
    <div
      className={cn(
        'bg-[#e60023] text-white block cursor-pointer badge w-auto text-base !pl-8',
        className
      )}
      onClick={onClick}
      style={{
        background:
          '#e60023 url(data:image/svg+xml;base64,) 3px 50% no-repeat',
        backgroundSize: '18px 18px',
        backgroundPosition: '8px 50%',
      }}
    >
      Save
    </div>
  );
};
