import { cn } from '@src/lib/helpers/helper';

declare global {
  interface Window {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    PinUtils: any;
  }
}

type Props = {
  src?: string;
  className?: string;
};

export const PinterestSaveButton = ({ className, src }: Props) => {
  if (!src) return null;
  const onClick = () => {
    window.PinUtils.pinOne({
      media: src,
    });
  };
  return (
    <span
      className={cn(
        'bg-[#e60023] text-white flex cursor-pointer badge left-badge h-6 w-12',
        className
      )}
      onClick={onClick}
      style={{
        background:
          '#e60023 url(data:image/svg+xml;base64,) 3px 50% no-repeat',
        textIndent: '20px',
        backgroundSize: '14px 14px',
        fontSize: '11px',
        padding: '4px 4px 0px 0px',
        borderRadius: '2px',
      }}
    >
      Save
    </span>
  );
};
